<!-- Apps --><?php    // load featured apps    $featuredApps = array();    $query = new EntityFieldQuery();    $query->entityCondition('entity_type', 'node')                    ->entityCondition('bundle', 'application')                    ->propertyCondition('status', 1)                    ->fieldCondition('field_featured', 'value', 1)                    ->range(0, 2)                    ->propertyOrderBy('created', 'desc');    $res = $query->execute();    if (isset($res['node'])) {        $nids = array_keys($res['node']);        $featuredApps = entity_load('node', $nids);    }    // load top rated apps    $topRatedApps = array();    $query = new EntityFieldQuery();    $query->entityCondition('entity_type', 'node')                    ->entityCondition('bundle', 'application')                    ->propertyCondition('status', 1)                    ->range(0, 4)                    ->fieldOrderBy('field_apps_rating', 'rating', 'desc');    $res = $query->execute();    if (isset($res['node'])) {        $nids = array_keys($res['node']);        $topRatedApps = entity_load('node', $nids);    }    // load recent apps    $recentApps = array();    $query = new EntityFieldQuery();    $query->entityCondition('entity_type', 'node')                    ->entityCondition('bundle', 'application')                    ->propertyCondition('status', 1)                    ->range(0, 12)                    ->propertyOrderBy('created', 'desc');    $res = $query->execute();    if (isset($res['node'])) {        $nids = array_keys($res['node']);        $recentApps = entity_load('node', $nids);    }?><div class="row applications-con">    <div class="large-6 columns apptitle">        <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua.</p>        <p>Nam liber tempor cum soluta nobis eleifend option congue nihil imperdiet doming id quod mazim placerat facer possim assum.</p>    </div>	<div class="large-1 columns"></div>    <div class="large-5 columns app-con">        <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua.</p>        <a href="/node/add/application" class="appsubmit">Submit app</a>    </div></div><div class="row featured">    <div class="large-6 columns  conntent-area">     	<h1>Featured</h1>    </div>    <div class="large-6 columns">        <ul class="social-icon">            <li><img alt="" src="/sites/all/themes/dgph/images/f-icon.png"></li>            <li>                <img alt="" src="sites/all/themes/dgph/images/t-icon.png" title="Twitter">            </li>            <li><img alt="" src="/sites/all/themes/dgph/images/icon-11.png"></li>            <li><img style= "float:left;margin-top:4px" src="/sites/all/themes/dgph/images/icon-20.png"  border="0" alt="Share"/></li>        </ul>    </div></div><div class="row applications-con">    <div class="featured-con">        <?php            for($i=0; $i<2; $i++) {                if (!empty($featuredApps)) {                    // show featured app                    $app = array_shift($featuredApps);                    $title = l($app->title, 'node/'.$app->nid);                    $text = $app->body['und'][0]['value'];                    if (strlen($text)>100) {                        $text = substr($text, 0, 100). ' ...';                    }                    $catId = $app->field_category['und'][0]['value'];                    $field = field_info_field('field_category');                    $catName = $field['settings']['allowed_values'][$catId];                    $rating = field_view_field('node', $app, 'field_apps_rating');                    $img = field_view_field('node', $app, 'field_screen_shot');        ?>        <div class="large-6 columns featured-area">            <div class="img-con"><?php print render($img) ?></div>            <h1><?php echo $title ?></h1>            <span><?php echo $catName ?></span>             <?php print render($rating) ?>            <p><?php echo $text ?></p>        </div>        <?php               }            }        ?>     </div></div><div class="row aapslist-new">    <div class="large-6 columns conntent-area  ">        <div class="news-video">            <h1>Top rated</h1>            <?php                while(!empty($topRatedApps)) {                    $app = array_shift($topRatedApps);                    $title = l($app->title, 'node/'.$app->nid);                    $text = $app->body['und'][0]['value'];                    if (strlen($text)>100) {                        $text = substr($text, 0, 100). ' ...';                    }                    $catId = $app->field_category['und'][0]['value'];                    $field = field_info_field('field_category');                    $catName = $field['settings']['allowed_values'][$catId];                    $rating = field_view_field('node', $app, 'field_apps_rating');                    $img = field_view_field('node', $app, 'field_screen_shot');                    $areaStyle = 'featured-area';                    if (empty($topRatedApps)) {                        $areaStyle = 'featured-area featured-area-last';                    }            ?>            <div class="<?php echo $areaStyle ?>">                <div class="img-con"><?php print render($img) ?></div>        	<h1><?php echo $title ?></h1>                <span><?php echo $catName ?></span>                 <?php print render($rating) ?>                <p><?php echo $text ?></p>            </div>            <?php                }            ?>            <div class="view">                <a style="float:left;" href="/apps/list">View All</a>            </div>        </div>    </div>    <div class="large-6 columns conntent-area latestnews">        <h1>New</h1>        <div class="evenodd-con">            <div class="evenodd-left">                <?php                    $appsLeft = 6;                    while(!empty($recentApps) && $appsLeft>0) {                        $app = array_shift($recentApps);                        $title = l($app->title, 'node/'.$app->nid);                        $catId = $app->field_category['und'][0]['value'];                        $field = field_info_field('field_category');                        $catName = $field['settings']['allowed_values'][$catId];                        $styleClass = (0==$appsLeft%2) ? 'even' : 'odd';                        $iconName = 'icn-'.strtolower(str_replace(' ', '_', $catName)).'.png';                ?>                <div class="<?php echo $styleClass ?>">                    <div class="img-con"><img src="/sites/all/themes/dgph/images/apps/<?php echo $iconName ?>" alt=""></div>                    <h2><?php echo $title ?></h2>                    <span><?php echo $catName ?></span>                </div>                <?php                        $appsLeft--;                    }                ?>            </div>            <div class="evenodd-left evenodd-left-last">                <?php                    $appsLeft = 6;                    while(!empty($recentApps) && $appsLeft>0) {                        $app = array_shift($recentApps);                        $title = l($app->title, 'node/'.$app->nid);                        $catId = $app->field_category['und'][0]['value'];                        $field = field_info_field('field_category');                        $catName = $field['settings']['allowed_values'][$catId];                        $styleClass = (0==$appsLeft%2) ? 'odd' : 'even';                        $iconName = 'icn-'.strtolower(str_replace(' ', '_', $catName)).'.png';                ?>                <div class="<?php echo $styleClass ?>">                    <div class="img-con"><img src="/sites/all/themes/dgph/images/apps/<?php echo $iconName ?>" alt=""></div>                    <h2><?php echo $title ?></h2>                    <span><?php echo $catName ?></span>                </div>                <?php                        $appsLeft--;                    }                ?>            </div>        </div>    </div></div><script src="http://connect.facebook.net/en_US/all.js"></script><script type='text/javascript'>FB.init({appId: "155199834685588", status: true, cookie: true});</script>