<!-- Infographics --><?php    global $user;    $is_editor = (in_array('editor', $user->roles) || in_array('administrator', $user->roles));    // Latest Visualizations    $visualizations_query = db_select('node', 'n')            ->fields('n', array('nid'))            ->condition('n.type', 'visualizations')            ->condition('n.status', 1)            ->orderBy('n.created', 'desc')            ->range(0, 4);    $nids = $visualizations_query->execute()->fetchCol();    $latestvisualizations = node_load_multiple($nids);?><?php if ($is_editor) { ?><div class="row news-con visualizations-con ">    <div class="large-12 columns">        <div class="twelve columns editor_console">            Update&nbsp;<a href='<?php echo base_path(); ?>infographics/apcpi/import'>PCPI</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/philgeps-import'>GEPS</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/dbm/import'>DBM</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/commodity/import'>Commodity</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/education/import'>Education</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/ched/import'>Commission</a>            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/dof/import'>Customs</a>	    &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/customs-viz/import'>Customs-Viz</a>	    <!-- Create link to import -->            &nbsp;|&nbsp;<a href='<?php echo base_path(); ?>infographics/philgeps-agency'>Agency</a>        </div>    </div></div><?php } ?><?php	$query = db_select('node', 'n')		->condition('type', 'slide_show')		->condition('status', '1')		->fields('n');	$query->leftjoin('field_revision_field_slide_weight', 'slide_order', 'slide_order.entity_id = n.nid');	$query->fields('slide_order');	$query->leftjoin('field_data_field_slide_type', 'tid', 'tid.entity_id = n.nid');	$query->fields('tid');	$query->condition('tid.field_slide_type_tid', 9, '=');	$query->orderBy('field_slide_weight_value', 'ASC');	$result = $query->execute();?>      <div id="banner-content" class="row">        <div id="banner" class="large-9 columns">          <ul data-orbit data-options="bullets:false;animation:fade">		    <?php			while($slide_nid = $result->fetchObject()){				$slide_node = node_load($slide_nid->nid);				$image = '';				if(sizeof($slide_node->field_slide_image)){					$image = '<img src="'.file_create_url($slide_node->field_slide_image['und'][0]['uri']).'" />';				}				$description = '';				if(sizeof($slide_node->field_slide_description)){					$description = $slide_node->field_slide_description['und'][0]['value'];				}				$editlink = '';				if(node_access('update',$slide_node)) {					$editlink = '<div class="node-edit">' . l('Edit', 'node/'.$slide_node->nid.'/edit', $options = array('query'=>array('destination'=>$_GET['q']))) . '</div>';				}			?>            <li> <?php print $image; ?>              <div class="orbit-caption">                <h2><?php print $slide_node->title ?></h2>                <h3><?php print $editlink; ?></h3>                <?php print $description; ?>              </div>            </li>			<?php } ?>          </ul>        </div>        <div class="large-3 columns visualizations headlines">        <h1>LATEST INFOGRAPHICS</h1>        <?php            $idx = 0;            $vmax = min(array(count($latestvisualizations), 4));            while(!empty($latestvisualizations) && $idx<$vmax) {                $entry = array_shift($latestvisualizations);                $title = l($entry->title, 'node/'.$entry->nid);                $text = $entry->body['und'][0]['value'];                $dt = date('M j, Y', $entry->created);                $last = (($idx>0) && (0==(($idx+1)%$vmax))) ? ' post-last' : '';        ?>        <div class="post<?php echo $last ?>">            <h2><?php print $title ?></h2>            <h3><?php print $dt.' / '.$entry->name ?></h3>                    </div>        <?php                 $idx++;            }         ?>        <div class="view">                <a style="float:left;" href="/latest-visualizations">View All</a>            </div>		</div>      </div><div class="row">    <div class="large-12 columns content-area">    <div class="visualizations-left">        <h1>Dashboards</h1>    </div>    </div>    <div class="large-7 columns content-area">        <div class="visualizations-full">            <a title="Open" class="yellow-setting" href="<?php echo base_path(); ?>infographics/procurement-compliance">yellow-setting</a>            <a title="Open" href="<?php echo base_path(); ?>infographics/procurement-compliance"><h2>Procurement Compliance & <br>Performance Indicators</h2></a>            <a title="Open" href="<?php echo base_path(); ?>infographics/procurement-compliance"><img src="<?php echo base_path(); ?>sites/all/themes/dgph/images/grap.png" alt="" align="middle"></a>            <div class="description-text">Procurement Compliance & Performance Indicators or PCPIs, are self-assessment tools of procuring entities to measure and evaluate their procurement practices. The process is overseen by the Policy Board. This dashboard features the PCPI performance of the executive agencies of a government.</div>        </div>        <div class="clear"></div>        <div class="visualizations-full">            <a title="Open" class="yellow-setting" href="<?php echo base_path(); ?>infographics/education">yellow-setting</a>            <a title="Open" href="<?php echo base_path(); ?>infographics/education"><h2>School Enrollment Dashboard</h2></a>            <a title="Open" href="<?php echo base_path(); ?>infographics/education"><img src="<?php echo base_path(); ?>sites/all/themes/dgph/images/education.jpg" alt=""></a>            <div class="description-text">This dashboard f