<!-- Latest Forums --><?php    // load the latest forums    $terms = advanced_forum_forum_load(0);    $forums = array();    $containers = array();    $topic = isset($_GET['t']) ? str_replace('+', ' ', $_GET['t']) : '';    $free = isset($_GET['f']) ? str_replace('+', ' ', $_GET['f']) : '';    foreach($terms->forums as $item) {        if (1==$item->depth) {            // get forum date / time            $tid = $item->tid;            $query = db_select('forum_index', 'f')                        ->fields('f', array('created'))                        ->condition('f.tid', $tid)                        ->orderBy('f.created', 'desc')                        ->range(0, 1);            $item->tm = $query->execute()->fetchColumn();            if (!empty($free)) {                $free = strtolower(trim($free));                if ((strpos(strtolower($item->name), $free)!==FALSE) || (strpos(strtolower($item->description), $free)!==FALSE)) {                    // add this item to the result                    $forums[$item->tid] = $item;                }            }            else if (!empty($topic)) {                 $parents = taxonomy_get_parents($tid);                 if (!empty($parents)) {                     $par = array_shift($parents);                     if ($par->name==$topic) {                        // add this item to the result                        $forums[$item->tid] = $item;                     }                 }            }            else {                // add this item to the result                $forums[$item->tid] = $item;            }        }        else if (0==$item->depth) {            $containers[$item->name] = $item;        }    }    //sort($containers, SORT_NUMERIC);    //krsort($forums, SORT_NUMERIC);?><div class="row news-con">    <div class="large-12 conntent-area">       <div class="news">           <h2>Forums</h2>       </div>    </div>    <div class="large-3 columns">    <br />    <br />    <br />        <div class="search-con">            <h3>Search by</h3>            <div class="search-area">                <input name="clander-search" type="image" src="/sites/all/themes/dgph/images/clander-icon.png" class="clander-btn" onclick='forumSearchClicked()'> <input id="forum-search-text" name="" type="text" class="clander-search" onkeydown='forumSearchKeydown(event)'>            </div>        </div>        <div id="accordion" class='sidebar-accordion'>            <h3>Topic</h3>            <div>                <p>                    <form class="custom">                        <?php                             foreach($containers as $container) {                                $tid = $container->tid;                                $cname = $container->name;                        ?>                        <label for="checkbox1"><input type="checkbox" id="topic_<?php echo $tid ?>" style="display: none;" target-uri='/forum/latest?t=<?php echo $cname ?>'<?php echo ($topic==$cname) ? " checked='checked'" : "" ?>><?php echo $cname ?></label>                        <?php } ?>                    </form>                </p>            </div>        </div>    </div>    <div class="large-6 column latestForums">        <h1>Latest Forums</h1>    </div>    <div class="large-3 column">         <ul class="social-icon">            <li class="ask"><a href="/node/add/forum/3">Ask Questions</a></li>        </ul>    </div>    <div class="large-9 columns latestforum grid">        <?php            $odd = false;            $count = count($forums);            $idx = 0;            foreach($forums as $forum) {                $tid = $forum->tid;                $tm = $forum->tm;                $year = ($tm>0) ? date('Y', $tm) : '';                $month = ($tm>0) ? date('M', $tm) : '';                $day = ($tm>0) ? date('d', $tm) : 'NA';                $postsCount = $forum->num_posts;                $name = $forum->name;                $text = $forum->description;                $last = ($idx == $count - 1);                $postClass = 'post-con';                if ($idx==$count-1) {                    // last post                    $postClass .= ' post-con-last';                }                else if ($odd) {                    // odd post                    $postClass .= ' post-con-old';                }        ?>                <div class="<?php echo $postClass ?>">                    <a href="#" class="comments-count"><?php echo $postsCount ?></a>                    <div class="date-con">                        <p><?php echo $month ?></p>                        <h5><?php echo $day ?></h5>                        <p><?php echo $year ?></p>                    </div>                    <h1><?php echo $name ?></h1>                    <p><?php echo $text ?></p>                    <a href="/forum/<?php echo $tid ?>" class="view-comm">View</a>                </div>        <?php                $odd = !$odd;                $idx++;            }        ?>        <div class="view">            <a style="float:left;" href="/forum">View All</a>        </div>    </div> </div>