<!-- Community --><?php    // load latest news    $query = db_select('node', 'n')            ->fields('n', array('nid'))            ->condition('n.type', 'blog')            ->condition('n.status', 1)            ->orderBy('n.created', 'desc')            ->range(0, 3);    $nids = $query->execute()->fetchCol();    $latestNews = node_load_multiple($nids);    // load the latest forums    $terms = advanced_forum_forum_load(0);    $forums = array();    foreach($terms->forums as $item) {        if (1==$item->depth) {            $forums[$item->tid] = $item;        }    }    krsort($forums, SORT_NUMERIC);?><?php	$query = db_select('node', 'n')		->condition('type', 'slide_show')		->condition('status', '1')		->fields('n');	$query->leftjoin('field_revision_field_slide_weight', 'slide_order', 'slide_order.entity_id = n.nid');	$query->fields('slide_order');	$query->leftjoin('field_data_field_slide_type', 'tid', 'tid.entity_id = n.nid');	$query->fields('tid');	$query->condition('tid.field_slide_type_tid', 10, '=');	$query->orderBy('field_slide_weight_value', 'ASC');	$result = $query->execute();?>      <div id="banner-content" class="row community-slide">        <div id="banner" class="large-12 columns">          <ul data-orbit data-options="bullets:false;animation:fade">		    <?php			while($slide_nid = $result->fetchObject()){				$slide_node = node_load($slide_nid->nid);				$image = '';				if(sizeof($slide_node->field_slide_image)){					$image = '<img src="'.file_create_url($slide_node->field_slide_image['und'][0]['uri']).'" />';				}				$description = '';				if(sizeof($slide_node->field_slide_description)){					$description = $slide_node->field_slide_description['und'][0]['value'];				}				$editlink = '';				if(node_access('update',$slide_node)) {					$editlink = '<div class="node-edit">' . l('Edit', 'node/'.$slide_node->nid.'/edit', $options = array('query'=>array('destination'=>$_GET['q']))) . '</div>';				}			?>            <li> <?php print $image; ?>              <div class="orbit-caption">                <h2><?php print $slide_node->title ?></h2>                <h3><?php print $editlink; ?></h3>                <?php print $description; ?>              </div>            </li>			<?php } ?>          </ul>        </div>    </div><?php$active_user_query = db_select('users', 'u')			->condition('status', '1')			->fields('u', array('uid', 'name', 'mail', 'picture'))			->orderBy('u.created', 'DESC')			->range(0, 11);$active_user    = $active_user_query->execute();?><div class="row mostactive-user">    <div class="large-12 columns">	  <div class="act-Users">        <h1>MOST RECENT USERS</h1>        <ul>		<?php		while($activeusers = $active_user->fetchObject()){			$recent_user   = user_load($activeusers->uid, $reset = FALSE);			$profile_link = 'user/'.$activeusers->uid;			$fname = '';			if(sizeof($recent_user->field_first_name)):				$fname = $recent_user->field_first_name['und'][0]['value'];			endif;			$lname = '';			if(sizeof($recent_user->field_last_name)):				$lname = $recent_user->field_last_name['und'][0]['value'];			endif;			$author_name = '';			if($fname || $lname):				$name = ucwords($fname).' '.ucwords($lname);			else: 				$name = $recent_user->name;			endif;			$picture = '';			if (!empty($recent_user->picture->uri)):				  $picture = $recent_user->picture->uri;			elseif(variable_get('user_picture_default', '')):				  $default_pic = explode('/', variable_get('user_picture_default', ''));				  $picture = 'public://pictures/'.$default_pic[(sizeof($default_pic)-1)];			endif;						$user_picture  = '<img class="adaptive-image" src="'.image_style_url('active_user', $picture).'" alt="" title="" />';		?>            <li>            	<a href="/<?php print $profile_link; ?>">                    <?php print $user_picture; ?>                    <p><?php print substr($name, 0, 12); ?></p>					                </a>            </li>		<?php		}		?>                    </ul>	  </div>    </div></div> <?php$testimonial_query = db_select('node', 'n')			->condition('type', 'testimonials')			->condition('status', '1')			->fields('n', array('nid'))			->orderBy('n.created', 'DESC')			->range(0, 1);$testi_result 	= $testimonial_query->execute();$testi_data     = $testi_result->fetchObject();$testi_node     = node_load($testi_data->nid, $vid = NULL, $reset = FALSE);$video_id = '';if(sizeof($testi_node->field_testimonial_video)):	$video_url = explode('/', $testi_node->field_testimonial_video['und'][0]['video_url']);	$video_id  = $video_url[(sizeof($video_url)-1)];endif;$video_placeholder_url = '';if(sizeof($testi_node->field_tst_video_placeholder)):	$video_placeholder_url = file_create_url($testi_node->field_tst_video_placeholder['und'][0]['uri']);endif;?><div class="row community-row">    <div class="large-6 columns conntent-area">        <div class="activities">            <h1>Testimonials</h1>			<div class="player">                            <!--<iframe width="100%" height="312px" src="//www.youtube.com/embed/<? print $video_id ?>?width=100%&amp;height=312px&amp;autoplay=0&amp;hd=1&amp;rel=0&amp;autohide=2&amp;showinfo=1&amp;modestbranding=0&amp;theme=dark&amp;iv_load_policy=1&amp;start=0&amp;wmode=opaque" frameborder="0" allowfullscreen></iframe>-->                            <img src='<?php echo $video_placeholder_url ?>' width="553px" height="312px">			</div>            <h2><?php   print l($testi_node->title, 'node/'.$testi_node->nid); ?></h2>			<?php			$teaser = '';			$teaser = render(field_view_field('node', $testi_node, 'body', array(			  'label'=>'hidden', 			  'type' => 'text_summary_or_trimmed',			  'settings'=>array('trim_length' => 350),			)));            print $teaser;			?>            <div class="view">                <a href="/testimonials" style="float:left;">View All</a>            </div>        </div>    </div>    <div class="large-3 columns conntent-area latestnews">        <h1>Latest News</h1>        <?php            $idx = 0;            while(!empty($latestNews) && $idx<3) {                $entry = array_shift($latestNews);                $title = l($entry->title, 'node/'.$entry->nid);                $text = $entry->body['und'][0]['value'];                if (strlen($text)>75) {                    $text = substr($text, 0, 75) . '...';                }                $dt = date('M j, Y', $entry->created);                $last = (($idx>0) && (0==(($idx+1)%3))) ? ' post-last' : '';        ?>        <div class="post<?php echo $last ?>">            <h2><?php echo $title ?></h2>            <h3><?php echo $dt.'<br>'.$entry->name ?></h3>            <?php echo  $text ?>        </div>        <?php                 $idx++;            }         ?>        <div class="view">            <a href="/news/latest" style="float:left;">View All</a>        </div>    </div>    <div class="large-3 columns conntent-area  forums">        <h1>Forums</h1>        <?php            $f1 = array_shift($forums);            $f2 = array_shift($forums);            $title1 = l($f1->name, 'forum/'.$f1->tid);            $title2 = l($f2->name, 'forum/'.$f2->tid);        ?>        <div class="post">            <h2><?php echo $title1 ?></h2>            <p><?php echo $f1->description ?></p>        </div>        <div class="post post-last">            <h2><?php echo $title2 ?></h2>            <p><?php echo $f2->description ?></p>        </div>        <div class="view">            <a href="/forum/latest" style="float:left;">View All</a>        </div>    </div></div>