<!-- Latest News --><?php    $query = db_select('node', 'n')            ->fields('n', array('nid'))            ->condition('n.type', 'blog')            ->condition('n.status', 1)            ->orderBy('n.created', 'desc');    $nids = $query->execute()->fetchCol();    $news = node_load_multiple($nids);?><style type='text/css'>    .post h2 a,    .news-video h2 a {        color: inherit !important;    }</style><!-- banner --><?php	$query = db_select('node', 'n')		->condition('type', 'slide_show')		->condition('status', '1')		->fields('n');	$query->leftjoin('field_revision_field_slide_weight', 'slide_order', 'slide_order.entity_id = n.nid');	$query->fields('slide_order');	$query->orderBy('field_slide_weight_value', 'ASC');	$result = $query->execute();?>      <div id="banner-content" class="row">        <div id="banner" class="large-9 columns">          <ul data-orbit data-options="bullets:false;animation:fade">		    <?php			while($slide_nid = $result->fetchObject()){				$slide_node = node_load($slide_nid->nid);				$image = '';				if(sizeof($slide_node->field_slide_image)){					$image = '<img src="'.file_create_url($slide_node->field_slide_image['und'][0]['uri']).'" />';				}				$description = '';				if(sizeof($slide_node->field_slide_description)){					$description = $slide_node->field_slide_description['und'][0]['value'];				}				$editlink = '';				if(node_access('update',$slide_node)) {					$editlink = '<div class="node-edit">' . l('Edit', 'node/'.$slide_node->nid.'/edit', $options = array('query'=>array('destination'=>$_GET['q']))) . '</div>';				}			?>            <li> <?php print $image; ?>              <div class="orbit-caption">                <h2><?php print $slide_node->title ?></h2>                <h3><?php print $editlink; ?></h3>                <?php print $description; ?>              </div>            </li>			<?php } ?>          </ul>        </div>        <div class="large-3 columns conntent-area headlines">        <h1>Headlines</h1>        <?php             $idx = 0;            foreach($news as $entry) {                if ($idx<4) {                    $nid = $entry->vid;                    $title = $entry->title;                    $dt = date('M j, Y', $entry->created);        ?>            <div class="post">                				<h2><?php print l($title, 'node/'.$nid); ?></h2>                <h3><?php echo $dt.' / '.$entry->name ?></h3>            </div>        <?php                 }                $idx++;            }         ?>		</div>      </div><div class="row">    <div class="large-6 columns conntent-area news-video-container">        <div class="news-video">			<div class="newsvideo-container">				<?php					$latestNews = array_shift(array_values($news));					$nid = $latestNews->vid;					$title = $latestNews->title;					$text = $latestNews->body['und'][0]['value'];                                        if (strlen($text)>400) {                                            $text = substr($text, 0, 400).' ...';                                        }					$image = '';					if(sizeof($latestNews->field_news_picture)){						$image = '<img class="adaptive-image" src="'.file_create_url($latestNews->field_news_picture['und'][0]['uri']).'" alt="" title="" />';					}				?>				<h1>News 01.</h1>				<?php print $image; ?>				<h2><?php print l($title, 'node/'.$nid); ?></h2>				<?php echo $text ?>			</div>            <div class="view">                <a href="/<?php print drupal_get_path_alias('node/'.$nid); ?>" style="float:left;">More</a>            </div>        </div>    </div>    <div class="large-6 columns conntent-area latestnews-header">        <h1>Latest News</h1>    </div>    <div class='large-6 columns conntent-area latestnews'>    <?php         $idx = 0;        foreach($news as $entry) { 		  if ($idx<4) {            if (($idx>0) && (0==($idx%3))) {                 //echo "</div><div class='three columns conntent-area latestnews'>";             }            $nid = $entry->vid;            $title = $entry->title;            $text = $entry->body['und'][0]['value'];            if (strlen($text)>75) {                $text = substr($text, 0, 75) . '...';            }            $dt = date('M j, Y', $entry->created);            $last = (($idx>0) && (0==(($idx+1)%3))) ? ' post-last' : '';    ?>        <div class="post<?php echo $last ?>">			<h2><?php print l($title, 'node/'.$nid); ?></h2>            <h3><?php echo $dt.'<br>'.$entry->name ?></h3>            <?php echo  $text ?>        </div>    <?php		  }            $idx++;        }     ?>    </div>    <div class="large-6 columns news-view">        <div class="view">            <a href="/blog" style="float:left;">View All</a>        </div>    </div></div>