<?phpif($id == 1 && $page == 0):    $query = db_select('node', 'n')            ->fields('n', array('nid'))            ->condition('n.type', 'blog')            ->condition('n.status', 1)            ->orderBy('n.created', 'desc');    $nids = $query->execute()->fetchCol();    $news = node_load_multiple($nids);?><!-- banner --><?php	$query = db_select('node', 'n')		->condition('type', 'slide_show')		->condition('status', '1')		->fields('n');	$query->leftjoin('field_revision_field_slide_weight', 'slide_order', 'slide_order.entity_id = n.nid');	$query->fields('slide_order');	$query->leftjoin('field_data_field_slide_type', 'tid', 'tid.entity_id = n.nid');	$query->fields('tid');	$query->condition('tid.field_slide_type_tid', 7, '=');	$query->orderBy('field_slide_weight_value', 'ASC');	$result = $query->execute();?>      <div id="banner-content" class="row">        <div id="banner" class="large-9 columns">          <ul data-orbit data-options="bullets:false;animation:fade">		    <?php			while($slide_nid = $result->fetchObject()){				$slide_node = node_load($slide_nid->nid);				$image = '';				if(sizeof($slide_node->field_slide_image)){					$image = '<img src="'.file_create_url($slide_node->field_slide_image['und'][0]['uri']).'" />';				}				$description = '';				if(sizeof($slide_node->field_slide_description)){					$description = $slide_node->field_slide_description['und'][0]['value'];				}				$editlink = '';				if(node_access('update',$slide_node)) {					$editlink = '<div class="node-edit">' . l('Edit', 'node/'.$slide_node->nid.'/edit', $options = array('query'=>array('destination'=>$_GET['q']))) . '</div>';				}			?>            <li> <?php print $image; ?>              <div class="orbit-caption">                <h2><?php print $slide_node->title ?></h2>                <h3><?php print $editlink; ?></h3>                <?php print $description; ?>              </div>            </li>			<?php } ?>          </ul>        </div>        <div class="large-3 columns conntent-area headlines">        <h1>Headlines</h1>        <?php 	$query_headlines = db_select('node', 'n')		->condition('type', 'blog')		->condition('status', '1')		->fields('n');	$query_headlines->leftjoin('field_revision_field_news_headlines', 'headlines', 'headlines.entity_id = n.nid');	$query_headlines->fields('headlines');		$query_headlines->condition('headlines.field_news_headlines_value', 'Yes', '=');	$query_headlines->orderBy('changed', 'ASC');	$query_headlines->range(0, 4);	$result_headlines = $query_headlines->execute();	if($result_headlines->rowCount()) {		$nids_headlines = $result_headlines->fetchCol();		$news_headlines = node_load_multiple($nids_headlines);            $idx = 0;            foreach($news_headlines as $entry) {                if ($idx<4) {                    $nid = $entry->vid;                    $title = $entry->title;                    $dt = date('M j, Y', $entry->created);        ?>            <div class="post">				<h2><?php print l($title, 'node/'.$nid); ?></h2>                <h3><?php echo $dt.' / '.$entry->name ?></h3>            </div>	<?php 				}				$idx++;			} 	}    ?>		</div>      </div><div class="row all-news-heading">	<div class="large-12 columns">		<h2 class="all-news-title"><?php print t('All News Articles'); ?></h2>	</div></div><?php endif; ?><?php if ($page == 0): ?><div class="large-3  bloglisting"><?php if(node_access('update',$node)) {	print '<div class="node-edit">' . l('Edit', 'node/'.$node->nid.'/edit', $options = array('query'=>array('destination'=>$_GET['q']))) . '</div>';}$submitted = '';if(sizeof($node->field_blog_submitted_by)){	$submitted = $node->field_blog_submitted_by['und'][0]['value'];}$teaser = '';if(sizeof($node->body)){	$teaser = render(field_view_field('node', $node, 'body', array(	  'label'=>'hidden', 	  'type' => 'text_summary_or_trimmed',	  'settings'=>array('trim_length' => 100),	)));}?>	<div class="blog-heading"><a href="<?php print $node_url; ?>"><?php print '<h2 class="title">'.$node->title.'</h2>'; ?></a></div>	<div class="blog-submitted"><?php print date('M d, Y', $node->created); print ($submitted) ? '<br />'.$submitted: ''; ?></div>	<?php if($teaser): ?>		<div class="blog-teaser"><?php print $teaser; ?></div>		<div class="bottom-border"></div>	<?php  endif; ?></div><!-- Blog Listing --><?php else: ?><?php// Blog Slide Show$blog_headlines = block_load('views', 'blog_headlines-block');$blog_headlines = drupal_render(_block_get_renderable_array(_block_render_blocks(array($blog_headlines))));?><div class="row blog-detailpage">	<div class="large-9 columns container">				<div class="description">			<?php			  // We hide the comments and links now so that we can render them later.			  hide($content['comments']);			  hide($content['links']);			  print render($content);			?>		</div>		<div class="clearfix">			<?php if (!empty($content['links'])): ?>			<div class="links linksdiv"><?php print render($content['links']); ?></div>			<?php endif; ?>			<?php print render($content['comments']); ?>		</div>	</div><!-- Slide Show -->	<div class="large-3 columns">		<?php print $blog_headlines; ?>	</div><!-- Headlines --></div><?php endif; ?>