<?php    global $user;    $is_editor = (in_array('editor', $user->roles) || in_array('administrator', $user->roles));        $vars = get_defined_vars();    $nid = $vars['nid'];    $dataHolder = isset($vars['field_data_holder'][0]) ? $vars['field_data_holder'][0]['value'] : '';    $dataDescription = isset($vars['field_data_description'][0]) ? $vars['field_data_description'][0]['value'] : '';    $detailsOfSuggestedUse = isset($vars['field_details_of_expected_use'][0]) ? $vars['field_details_of_expected_use'][0]['value'] : '';    // get labels for multi-valued fields    $statusField = field_info_field('field_status');    $statusLabels = $statusField['settings']['allowed_values'];    $orgField = field_info_field('field_organization_type');    $orgLabels = $orgField['settings']['allowed_values'];    $pubField = field_info_field('field_publication_preference');    $pubLabels = $pubField['settings']['allowed_values'];    $useField = field_info_field('field_intended_use');    $useLabels = $useField['settings']['allowed_values'];?><div class="row news-con">        <div class="large-6 columns subpage-title">            <h1><?php print render($title) ?></h1>        </div>        <div class="large-6 columns">            <ul class="social-icon">                <?php if ($is_editor) { echo "<li><a href='/node/$nid/edit'><img src='/sites/all/themes/dgph/images/edit-icon.png' title='Edit this data request' alt='Edit'></a></li>"; } ?>                <li><a href="/data-requests/list"><img src="/sites/all/themes/dgph/images/go-back-icon-md.png" title='Back to data requests list' alt="Back"></a></li>            </ul>        </div>        <div class="clear"></div></div><div class="row news-con">    <div class="for-data-request">            <div class="large-3 columns datarequest">                <div class="picturup">                    <h1>Requested by</h1>                    <div class="requested-by-info">                        <label>Name</label>                        <input name="field_requested_by_name" type="text" value="<?php echo $vars['field_your_name'][0]['value'] ?>" disabled='disabled'>                        <label>Email</label>                         <input name="field_requested_by_email" type="text" value="<?php echo $vars['field_your_email_address'][0]['value'] ?>" disabled='disabled'>                    </div>                </div>            </div>            <div class="large-6 columns  subpage-datarequest datarequest">                <h1>Data Request</h1>                <div class="request">                    <label>Data Holder</label>                    <input name="field_data_holder" type="text" value="<?php echo $dataHolder ?>" disabled='disabled'>                    <div class="clear"></div>                    <label>Data Description</label>                    <textarea name="field_data_description" rows="5" disabled='disabled'><?php echo $dataDescription ?></textarea>                    <div class="clear"></div>                    <label>Details of suggested use</label>                    <textarea name="field_details_of_expected_use" rows="5" disabled='disabled'><?php echo $detailsOfSuggestedUse ?></textarea>                    <div class="clear"></div>                </div>            </div>            <div class="large-3 columns subpage-datarequest-rightside datarequest_right">                <h3>Organization Type</h3>                <p><?php echo $orgLabels[$vars['field_organization_type'][0]['value']] ?></p>                   <h3>Publication preference </h3>                <p><?php echo $pubLabels[$vars['field_publication_preference'][0]['value']] ?></p>                   <h3>Suggested use</h3>                <ul>                    <?php                         foreach($vars['field_intended_use'] as $use) {                            echo "<li>".$useLabels[$use['value']]."</li>";                        }                     ?>                </ul>                   <h3>Status</h3>                               <?php                    $st = $vars['field_status'][0]['value'];                    echo "<p>".$statusLabels[$st];                    if (intval($st)==5) {                        // show the reason for declining the dataset                        $reason = $vars['field_decline_reason'][0]['value'];                        if (!empty($reason)) {                            echo " ($reason)";                        }                    }                    echo "</p>";                ?>            </div>    </div></div>